package aaa;
import java.util.*;
public class number {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n;
		Scanner s=new Scanner(System.in);
		n=s.nextInt();
		int count=0;
		for(int i=1;i<=15;i++){
			if(n%2!=0){
				count++;}
				System.out.println("count the odd number"+count);
			}
			
		}

	}


